var assert = require('assert');
var parser = require('./');

var out1 = parser.read("WED @LAWingCoTruck  915 Wilshire at #Figueora 90017 rear of building #LUNCH OPEN 1130-230 #DowntownFoodTruck");
assert.equal(out1.zip,"90017","zip 1 not correctly parsing");
assert.equal(out1.address,"915 Wilshire at","address 1 not correctly parsing");
assert.equal(out1.notes,"rear of building","notes not correctly parsing");

var out2 = parser.read("WED @gstacospotwheel  #WestwoodVillage GLENDON/Wilshire 90024 #LUNCH OPEN 1130-230PM");
assert.equal(out2.zip,"90024","zip 2 not correctly parsing");
assert.equal(out2.address,"GLENDON/Wilshire","address 2 not correctly parsing");

var out3 = parser.read("WED @LetsBowlIt_LA  @_Lantana_ NORTH 3000 Olympic #Samo 90404 #LUNCH OPEN 1130-230PM");
assert.equal(out3.zip,"90404","zip 3 not correctly parsing");
assert.equal(out3.address,"3000 Olympic","address 3 not correctly parsing");
